function initializeJS() {
  //for sidebar
 jQuery("#sidebar").niceScroll({styler:"fb",cursorcolor:"#ccc", cursorwidth: '7', cursorborderradius: '2px', background: '#F7F7F7', cursorborder: ''});
 jQuery(".list-items").niceScroll({styler:"fb",cursorcolor:"#ccc", cursorwidth: '7', cursorborderradius: '2px', background: 'transparent', cursorborder: ''});
}
jQuery(document).ready(function(){
    initializeJS();
});

// sub-menu
$(function() {
	var Accordion = function(el, multiple) {
		this.el = el || {};
		this.multiple = multiple || false;
		// Variables privadas
		var links = this.el.find('.sub-menu-link');
		// Evento
		links.on('click', {el: this.el, multiple: this.multiple}, this.dropdown)
	}
	Accordion.prototype.dropdown = function(e) {
		var $el = e.data.el;
			$this = $(this),
			$next = $this.next();
		$next.slideToggle();
		$this.parent().toggleClass('open');

		if (!e.data.multiple) {
			$el.find('.sub-menu').not($next).slideUp().parent().removeClass('open');
		};
	}	
	var accordion = new Accordion($('.sidebar-menu'), false);
});

